import React, { useState } from 'react';
import { Search, Bell, Plus } from 'lucide-react';
import Sidebar from './Sidebar/Sidebar';
import BodyChart from './Charts/BodyChart';
import ActivityChart from './Charts/ActivityChart';
import Calendar from './Calendar/Calendar';
import AppointmentsList from './Appointments/AppointmentsList';
import UpcomingSchedule from './Schedule/UpcomingSchedule';

const Dashboard = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  return (
    <div className="min-h-screen bg-[#E2EBF8] p-6">
      <div className="flex bg-white rounded-[30px] overflow-hidden min-h-[calc(100vh-48px)]">
        {/* Sidebar - 20% */}
        <div className="w-[20%]">
          <div className="h-full bg-[#F6FAFF] p-6">
            <Sidebar 
              isMobileMenuOpen={isMobileMenuOpen} 
              onCloseMobileMenu={() => setIsMobileMenuOpen(false)} 
            />
          </div>
        </div>

        {/* Main Content - 45% */}
        <div className="w-[45%]">
          {/* Header Row */}
          <div className="p-6 pb-4">
            {/* Search */}
            <div className="relative w-full">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search"
                className="w-full pl-10 pr-12 py-2 border border-gray-100 rounded-lg text-sm outline-none"
              />
              {/* Bell Icon */}
              <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                <Bell className="w-5 h-5 text-gray-600" />
                <div className="absolute -top-1 -right-1 w-2 h-2 bg-[#2B3674] rounded-full"></div>
              </div>
            </div>
          </div>

          {/* Body Chart Section */}
          <div className="px-6 mb-6">
            <BodyChart />
          </div>

          {/* Activity Chart */}
          <div className="px-6">
            <ActivityChart />
          </div>
        </div>

        {/* Right Section - 35% */}
        <div className="w-[35%] bg-[#F6FAFF]">
          {/* Profile Section */}
          <div className="flex justify-end p-6 mb-8">
            <div className="flex items-center space-x-3">
              <div className="w-9 h-9 bg-[#0DBBFC] rounded-full flex items-center justify-center">
                <span className="text-white text-sm font-medium">JD</span>
              </div>
              <button className="w-9 h-9 bg-[#2B3674] rounded-full flex items-center justify-center">
                <Plus className="w-4 h-4 text-white" />
              </button>
            </div>
          </div>

          {/* Calendar */}
          <div className="px-6">
            <div className="bg-white rounded-xl shadow-sm p-4 mb-6">
              <Calendar />
            </div>

            {/* Appointments */}
            <div className="space-y-4 mb-6">
              <AppointmentsList />
            </div>

            {/* Upcoming Schedule */}
            <div className="bg-white rounded-xl shadow-sm p-4">
              <UpcomingSchedule />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
