import React, { useEffect } from 'react';
import { Calendar, MessageCircle, HelpCircle, Settings } from 'lucide-react';
import NavItem from './NavItem';
import SidebarSection from './SidebarSection';

const Sidebar = ({ isOpen, onClose }) => {
  // Prevent background scrolling when mobile menu is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isOpen]);

  // Simple function to handle backdrop click
  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <>
      {/* Backdrop overlay */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black/50 z-20 lg:hidden" 
          onClick={handleBackdropClick}
        />
      )}

      {/* Main sidebar */}
      <aside className={`
        fixed top-0 left-0 h-screen bg-white shadow-sm z-30
        w-[280px] lg:w-64 
        transform transition-transform duration-200
        lg:translate-x-0 lg:static
        ${isOpen ? 'translate-x-0' : '-translate-x-full'}
      `}>
        <div className="flex flex-col h-full">
          <div className="p-6">
            <h1 className="text-lg font-semibold tracking-wide">
              <span className="text-cyan-500">Health</span>
              <span className="text-gray-700">care</span>
            </h1>
          </div>
          
          <div className="flex-1 lg:flex lg:flex-col">
            {/* Mobile: scrollable, Desktop: all content visible */}
            <nav className="overflow-y-auto lg:overflow-visible">
              <SidebarSection title="General">
                <NavItem 
                  href="/dashboard" 
                  icon={
                    <div className="grid grid-cols-2 gap-[2px] w-full h-full">
                      <div className="bg-blue-600 rounded-sm"></div>
                      <div className="bg-blue-600 rounded-sm"></div>
                      <div className="bg-blue-600 rounded-sm"></div>
                      <div className="bg-blue-600 rounded-sm"></div>
                    </div>
                  }
                  isActive
                >
                  Dashboard
                </NavItem>
                
                <NavItem 
                  href="/history" 
                  icon={
                    <svg viewBox="0 0 20 20" fill="currentColor" className="w-5 h-5">
                      <path d="M3 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" />
                    </svg>
                  }
                >
                  History
                </NavItem>
                
                <NavItem href="/calendar" icon={<Calendar />}>
                  Calendar
                </NavItem>
                
                <NavItem 
                  href="/appointments" 
                  icon={
                    <svg viewBox="0 0 20 20" fill="currentColor" className="w-5 h-5">
                      <path d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1z" />
                    </svg>
                  }
                >
                  Appointments
                </NavItem>
                
                <NavItem 
                  href="/stats" 
                  icon={
                    <svg viewBox="0 0 20 20" fill="currentColor" className="w-5 h-5">
                      <path d="M2 11a1 1 0 011-1h2a1 1 0 011 1v5a1 1 0 01-1 1H3a1 1 0 01-1-1v-5zM8 7a1 1 0 011-1h2a1 1 0 011 1v9a1 1 0 01-1 1H9a1 1 0 01-1-1V7zM14 4a1 1 0 011-1h2a1 1 0 011 1v12a1 1 0 01-1 1h-2a1 1 0 01-1-1V4z" />
                    </svg>
                  }
                >
                  Statistics
                </NavItem>
              </SidebarSection>
              
              <SidebarSection title="Tools">
                <NavItem href="/chat" icon={<MessageCircle />}>
                  Chat
                </NavItem>
                
                <NavItem href="/support" icon={<HelpCircle />}>
                  Support
                </NavItem>
              </SidebarSection>
            </nav>
          </div>

          <div className="border-t border-gray-100 p-4 bg-white mt-auto">
            <NavItem href="/settings" icon={<Settings />}>
              Settings
            </NavItem>
          </div>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
