import React from 'react';
import { Heart, Eye, Pencil, User } from 'lucide-react';

const UpcomingSchedule = () => {
  const appointments = [
    {
      title: 'Dentist',
      time: '09:00-11:00',
      doctor: 'Dr. Cameron Williamson',
      type: 'dental',
      style: 'bg-indigo-600 text-white'
    },
    {
      title: 'Physiotherapy Appointment',
      time: '11:00-12:00',
      doctor: 'Dr. Kevin Djones',
      type: 'physio',
      style: 'bg-indigo-50 text-indigo-700'
    }
  ];

  const upcomingSchedule = [
    {
      day: 'Thursday',
      appointments: [
        {
          title: 'Health checkup complete',
          time: '11:00 AM',
          icon: <Pencil className="w-4 h-4 text-gray-600" />
        },
        {
          title: 'Ophthalmologist',
          time: '14:00 PM',
          icon: <Eye className="w-4 h-4 text-gray-600" />
        }
      ]
    },
    {
      day: 'Saturday',
      appointments: [
        {
          title: 'Cardiologist',
          time: '12:00 AM',
          icon: <Heart className="w-4 h-4 text-red-500 fill-current" />
        },
        {
          title: 'Neurologist',
          time: '16:00 PM',
          icon: <User className="w-4 h-4 text-gray-600" />
        }
      ]
    }
  ];

  return (
    <div className="space-y-6">
      {/* Current Appointments */}
      <div className="flex space-x-2">
        {appointments.map((apt, idx) => (
          <div key={idx} className={`${apt.style} flex-1 p-4 rounded-2xl`}>
            <div className="flex justify-between items-start">
              <div>
                <h4 className="font-medium text-base mb-1">{apt.title}</h4>
                <p className="text-sm opacity-90">{apt.time}</p>
                <p className="text-sm opacity-90">{apt.doctor}</p>
              </div>
              {apt.type === 'dental' && (
                <div className="w-8 h-8 bg-white bg-opacity-20 rounded-lg flex items-center justify-center">
                  <div className="w-4 h-5 bg-white rounded-t-full"></div>
                </div>
              )}
              {apt.type === 'physio' && (
                <div className="w-8 h-8 bg-indigo-100 rounded-lg flex items-center justify-center">
                  <span className="text-indigo-600">⚡</span>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Upcoming Schedule */}
      <div>
        <h3 className="text-lg font-semibold text-gray-900 mb-4">The Upcoming Schedule</h3>
        
        <div className="space-y-4">
          {upcomingSchedule.map((schedule, idx) => (
            <div key={idx}>
              <p className="text-sm text-gray-500 mb-2">On {schedule.day}</p>
              <div className="flex space-x-3">
                {schedule.appointments.map((apt, aptIdx) => (
                  <div key={aptIdx} className="flex-1 bg-indigo-50/60 rounded-2xl p-3">
                    <div className="flex justify-between items-start">
                      <div>
                        <h4 className="text-sm font-medium text-gray-900 mb-1">{apt.title}</h4>
                        <p className="text-xs text-gray-500">{apt.time}</p>
                      </div>
                      <div className="w-6 h-6 bg-white rounded-lg flex items-center justify-center">
                        {apt.icon}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default UpcomingSchedule;
