import React, { useState } from 'react';
import { useHealth } from '../../context/HealthContext';
import { Pill, Plus, Check, Clock } from 'lucide-react';

const MedicationCard = ({ medication, onTake }) => {
  const { name, dosage, frequency, timeSlots, taken } = medication;

  return (
    <div className={`bg-white rounded-xl p-4 shadow-sm ${taken ? 'opacity-75' : ''}`}>
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center">
          <div className="w-10 h-10 bg-[#6C49FF] rounded-lg flex items-center justify-center mr-3">
            <Pill className="w-6 h-6 text-white" />
          </div>
          <div>
            <h3 className="font-semibold text-gray-800">{name}</h3>
            <p className="text-sm text-gray-500">{dosage} • {frequency}</p>
          </div>
        </div>
        {!taken && (
          <button
            onClick={() => onTake(medication.id)}
            className="bg-[#0DBBFC] text-white p-2 rounded-lg hover:bg-[#0CA8E3] transition-colors"
          >
            <Check className="w-5 h-5" />
          </button>
        )}
      </div>
      <div className="flex items-center text-sm text-gray-500">
        <Clock className="w-4 h-4 mr-1" />
        {timeSlots.join(', ')}
      </div>
    </div>
  );
};

const AddMedicationModal = ({ isOpen, onClose, onAdd }) => {
  const [newMed, setNewMed] = useState({
    name: '',
    dosage: '',
    frequency: 'Daily',
    timeSlots: ['09:00 AM']
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd({
      ...newMed,
      id: Date.now(),
      taken: false
    });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl p-6 w-full max-w-md">
        <h2 className="text-xl font-bold text-gray-800 mb-4">Add Medication</h2>
        <form onSubmit={handleSubmit}>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Medication Name
              </label>
              <input
                type="text"
                value={newMed.name}
                onChange={(e) => setNewMed({ ...newMed, name: e.target.value })}
                className="w-full p-2 border rounded-lg"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Dosage
              </label>
              <input
                type="text"
                value={newMed.dosage}
                onChange={(e) => setNewMed({ ...newMed, dosage: e.target.value })}
                className="w-full p-2 border rounded-lg"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Frequency
              </label>
              <select
                value={newMed.frequency}
                onChange={(e) => setNewMed({ ...newMed, frequency: e.target.value })}
                className="w-full p-2 border rounded-lg"
              >
                <option>Daily</option>
                <option>Twice Daily</option>
                <option>Weekly</option>
              </select>
            </div>
          </div>
          <div className="mt-6 flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-[#0DBBFC] text-white rounded-lg hover:bg-[#0CA8E3]"
            >
              Add Medication
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const Medications = () => {
  const { state, takeMedication } = useHealth();
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-xl font-bold text-gray-800">Medications</h2>
        <button
          onClick={() => setIsModalOpen(true)}
          className="flex items-center text-[#0DBBFC] hover:underline"
        >
          <Plus className="w-4 h-4 mr-1" />
          Add New
        </button>
      </div>
      <div className="space-y-4">
        {state.medications.map((medication) => (
          <MedicationCard
            key={medication.id}
            medication={medication}
            onTake={takeMedication}
          />
        ))}
      </div>
      <AddMedicationModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onAdd={(newMed) => {
          // Here you would typically dispatch an action to add the medication
          console.log('Adding medication:', newMed);
          setIsModalOpen(false);
        }}
      />
    </div>
  );
};

export default Medications;
