import React, { useState } from 'react';
import { useHealth } from '../../context/HealthContext';
import { Bell, X } from 'lucide-react';

const NotificationItem = ({ notification }) => {
  const { title, message, time, type } = notification;

  const getTypeStyles = (type) => {
    switch (type) {
      case 'appointment':
        return 'bg-blue-50 border-blue-200';
      case 'medication':
        return 'bg-purple-50 border-purple-200';
      case 'alert':
        return 'bg-red-50 border-red-200';
      default:
        return 'bg-gray-50 border-gray-200';
    }
  };

  return (
    <div className={`p-4 border-l-4 ${getTypeStyles(type)} rounded-r-lg mb-2`}>
      <h4 className="font-semibold text-gray-800">{title}</h4>
      <p className="text-sm text-gray-600 mt-1">{message}</p>
      <span className="text-xs text-gray-500 mt-2 block">{time}</span>
    </div>
  );
};

const Notifications = () => {
  const { state } = useHealth();
  const [isOpen, setIsOpen] = useState(false);

  const unreadCount = state.notifications.filter(n => !n.read).length;

  return (
    <div className="relative">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="relative p-2 hover:bg-gray-100 rounded-lg"
      >
        <Bell className="w-6 h-6 text-gray-600" />
        {unreadCount > 0 && (
          <span className="absolute top-0 right-0 w-2 h-2 bg-red-500 rounded-full"></span>
        )}
      </button>

      {isOpen && (
        <div className="absolute right-0 mt-2 w-80 bg-white rounded-xl shadow-lg border z-50">
          <div className="flex items-center justify-between p-4 border-b">
            <h3 className="font-semibold text-gray-800">Notifications</h3>
            <button
              onClick={() => setIsOpen(false)}
              className="text-gray-500 hover:text-gray-700"
            >
              <X className="w-5 h-5" />
            </button>
          </div>
          <div className="p-4 max-h-96 overflow-y-auto">
            {state.notifications.length > 0 ? (
              state.notifications.map((notification, index) => (
                <NotificationItem key={index} notification={notification} />
              ))
            ) : (
              <p className="text-gray-500 text-center py-4">No notifications</p>
            )}
          </div>
          {state.notifications.length > 0 && (
            <div className="p-4 border-t">
              <button className="text-[#0DBBFC] text-sm hover:underline w-full text-center">
                Mark all as read
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Notifications;
