import React from 'react';

// Navigation item component for sidebar links
const NavItem = ({ href, icon, children, isActive }) => {
  // Build the class names based on active state
  const linkClasses = [
    'flex items-center py-2.5 px-5',
    'transition-colors duration-150',
    isActive 
      ? 'text-blue-600 bg-blue-50 border-r-2 border-blue-600 font-medium' 
      : 'text-slate-600 hover:bg-slate-50'
  ].join(' ');

  const iconWrapperClasses = [
    'flex items-center justify-center',
    'w-5 h-5 mr-3',
    isActive ? 'text-blue-600' : 'text-slate-500'
  ].join(' ');

  return (
    <a href={href} className={linkClasses}>
      <span className={iconWrapperClasses}>
        {icon}
      </span>
      <span>{children}</span>
    </a>
  );
};

export default NavItem;
