import React from 'react';

// Navigation item component for sidebar links
const NavItem = ({ href, icon, children, isActive }) => {
  // Base styles that are always applied
  const baseStyles = 'flex items-center py-2.5 px-5 transition-colors duration-150';

  // Styles that change based on active state
  const activeStyles = isActive 
    ? 'text-blue-500 bg-blue-50 border-r-2 border-blue-500 font-medium'
    : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900';

  return (
    <a 
      href={href} 
      className={`${baseStyles} ${activeStyles}`}
    >
      {/* Icon wrapper to maintain consistent sizing */}
      <span className="w-5 h-5 mr-3 flex items-center justify-center">
        {icon}
      </span>

      {/* Text label */}
      <span className="text-sm">
        {children}
      </span>
    </a>
  );
};

export default NavItem;
