import React from 'react';

const ActivityChart = () => {
  const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  const getRandomHeight = (min, max) => {
    return min + Math.random() * (max - min);
  };

  return (
    <div className="bg-white rounded-xl shadow p-6">
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-6">
        <h3 className="text-xl font-bold text-gray-800 mb-2 sm:mb-0">Activity</h3>
        <div className="text-sm text-gray-500">3 appointments this week</div>
      </div>

      <div className="h-64 border-t border-gray-200 pt-4">
        <div className="flex items-end justify-between h-full space-x-4">
          {days.map((day) => (
            <div key={day} className="flex flex-col items-center flex-1">
              <div className="flex-1 flex flex-col justify-end w-full">
                <div className="flex items-end justify-center space-x-1 w-full">
                  <div 
                    className="w-2 sm:w-3 bg-cyan-400 rounded-t transition-all duration-300" 
                    style={{ height: `${getRandomHeight(40, 100)}px` }}
                  ></div>
                  <div 
                    className="w-2 sm:w-3 bg-blue-600 rounded-t transition-all duration-300" 
                    style={{ height: `${getRandomHeight(40, 100)}px` }}
                  ></div>
                  <div 
                    className="w-2 sm:w-3 bg-purple-500 rounded-t transition-all duration-300" 
                    style={{ height: `${getRandomHeight(40, 100)}px` }}
                  ></div>
                </div>
              </div>
              <div className="text-sm text-gray-600 mt-2">{day}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Legend */}
      <div className="flex justify-center mt-6 space-x-6 text-sm text-gray-600">
        <div className="flex items-center">
          <div className="w-4 h-4 bg-cyan-400 rounded mr-2"></div>
          <span>Checkups</span>
        </div>
        <div className="flex items-center">
          <div className="w-4 h-4 bg-blue-600 rounded mr-2"></div>
          <span>Appointments</span>
        </div>
        <div className="flex items-center">
          <div className="w-4 h-4 bg-purple-500 rounded mr-2"></div>
          <span>Tests</span>
        </div>
      </div>
    </div>
  );
};

export default ActivityChart;
