import React from 'react';

const ActivityChart = () => {
  const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
  
  const getRandomHeight = (min, max) => {
    return `${min + Math.random() * max}px`;
  };

  return (
    <>
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-gray-800 mb-2 sm:mb-0">Activity</h3>
        <div className="text-sm text-gray-500">3 appointment on this week</div>
      </div>
      
      <div className="h-48">
        <div className="flex items-end justify-between h-full space-x-1 sm:space-x-2">
          {days.map((day) => (
            <div key={day} className="flex flex-col items-center flex-1 min-w-0">
              <div className="flex-1 flex items-end pb-2 w-full">
                <div className="flex space-x-0.5 sm:space-x-1 w-full justify-center">
                  <div 
                    className="w-1 sm:w-2 bg-cyan-400 rounded-t" 
                    style={{height: getRandomHeight(20, 80)}}
                  ></div>
                  <div 
                    className="w-1 sm:w-2 bg-blue-600 rounded-t" 
                    style={{height: getRandomHeight(30, 70)}}
                  ></div>
                  <div 
                    className="w-1 sm:w-2 bg-purple-500 rounded-t" 
                    style={{height: getRandomHeight(10, 90)}}
                  ></div>
                </div>
              </div>
              <div className="text-xs text-gray-500 mt-2">{day}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Legend */}
      <div className="flex justify-center mt-6 space-x-4 text-xs text-gray-500">
        <div className="flex items-center">
          <div className="w-3 h-3 bg-cyan-400 rounded mr-2"></div>
          <span>Checkups</span>
        </div>
        <div className="flex items-center">
          <div className="w-3 h-3 bg-blue-600 rounded mr-2"></div>
          <span>Appointments</span>
        </div>
        <div className="flex items-center">
          <div className="w-3 h-3 bg-purple-500 rounded mr-2"></div>
          <span>Tests</span>
        </div>
      </div>
    </>
  );
};

export default ActivityChart;
