import React from 'react';
import { useHealth } from '../../context/HealthContext';
import { Heart, Footprints, Moon, Droplets, Pizza } from 'lucide-react';

const MetricCard = ({ title, current, goal, icon, color, unit }) => {
  const percentage = (current / goal) * 100;
  
  return (
    <div className="bg-white rounded-xl p-4 shadow-sm">
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center">
          <div className={`w-10 h-10 ${color} rounded-lg flex items-center justify-center mr-3`}>
            {icon}
          </div>
          <div>
            <h3 className="font-semibold text-gray-800">{title}</h3>
            <p className="text-sm text-gray-500">
              {current}{unit} of {goal}{unit}
            </p>
          </div>
        </div>
        <div className="text-lg font-bold text-[#2B3674]">
          {Math.round(percentage)}%
        </div>
      </div>
      <div className="w-full bg-gray-100 rounded-full h-2">
        <div
          className={`h-2 rounded-full transition-all duration-500 ${color}`}
          style={{ width: `${percentage}%` }}
        />
      </div>
    </div>
  );
};

const HealthMetrics = () => {
  const { state } = useHealth();
  const { healthMetrics } = state;

  const metrics = [
    {
      title: 'Steps',
      current: healthMetrics.steps.current,
      goal: healthMetrics.steps.goal,
      icon: <Footprints className="w-6 h-6 text-white" />,
      color: 'bg-[#0DBBFC]',
      unit: ''
    },
    {
      title: 'Sleep',
      current: healthMetrics.sleep.current,
      goal: healthMetrics.sleep.goal,
      icon: <Moon className="w-6 h-6 text-white" />,
      color: 'bg-[#2B3674]',
      unit: 'h'
    },
    {
      title: 'Water',
      current: healthMetrics.water.current,
      goal: healthMetrics.water.goal,
      icon: <Droplets className="w-6 h-6 text-white" />,
      color: 'bg-[#0DBBFC]',
      unit: 'L'
    },
    {
      title: 'Calories',
      current: healthMetrics.calories.current,
      goal: healthMetrics.calories.goal,
      icon: <Pizza className="w-6 h-6 text-white" />,
      color: 'bg-[#6C49FF]',
      unit: 'kcal'
    }
  ];

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-xl font-bold text-gray-800">Daily Goals</h2>
        <button className="text-[#0DBBFC] text-sm hover:underline">
          View Details â†’
        </button>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {metrics.map((metric) => (
          <MetricCard key={metric.title} {...metric} />
        ))}
      </div>
    </div>
  );
};

export default HealthMetrics;
